import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import NewOrderForm from './newOrderForm';

const NewExcelOrderForm = () => {
  const [groupForms, setGroupForms] = useState([]);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();
    reader.onload = (e) => {
      const data = new Uint8Array(e.target.result);
      const workbook = XLSX.read(data, { type: 'array' });
      const worksheet = workbook.Sheets[workbook.SheetNames[0]];
      const excelData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

      // Assuming the headers are in the order: 'Модел', 'Фолио', 'Височина', 'Ширина', 'Брой', 'м2', 'Профил', 'Дръжка'
      const [modelHeader, folioHeader, heightHeader, widthHeader, quantityHeader, areaHeader, profilHeader, handleHeader] = excelData[0];
      const formData = excelData.slice(1).map((row) => ({
        modelName: row[modelHeader],
        folioName: row[folioHeader],
        height: row[heightHeader],
        width: row[widthHeader],
        number: row[quantityHeader],
        area: row[areaHeader],
        profilName: row[profilHeader],
        handleName: row[handleHeader],
      }));

      setGroupForms(formData); // Update the groupForms state with the imported data
    };
    reader.readAsArrayBuffer(file);
  };

  const handleImportButtonClick = () => {
    // Check if there are any rows in the imported data
    if (groupForms.length > 0) {
      // If there is more than one row, create new groups
      const newGroups = groupForms.map((formData) => ({ ...formData }));
      setGroupForms((prevGroupForms) => [...prevGroupForms, ...newGroups]);
    }
  };

  // Rest of the form rendering code...
  return (
    <>
      <div className="col-span-2">
        <label htmlFor="fileInput" className="block font-medium">
          Импортиране на Excel файл:
        </label>
        <input
          type="file"
          id="fileInput"
          name="fileInput"
          onChange={handleFileChange}
          accept=".xlsx, .xls"
          className="mt-1"
        />
        <button
          className="px-4 py-2 mt-2 text-white bg-blue-600 rounded-md shadow-md hover:bg-blue-700"
          onClick={handleImportButtonClick}
        >
          Импортирай
        </button>
      </div>
      <div className="grid gap-2 mb-12 md:grid-cols-2">
        <div className="col-span-12 text-center">
          <PageTitle>Създаване на нова поръчка</PageTitle>
        </div>
        {groupForms.map((formData, index) => (
          <div key={index} className="col-span-2">
            <form
              id={`orderForm${index}`}
              className="grid grid-cols-2 gap-4 border"
              style={{ padding: '20px' }}
            >
              {/* Model Name */}
              <div>
                <label htmlFor="modelName" className="block font-medium">
                  Модел:
                </label>
                <select
                  className="mt-1 w-full p-2 border rounded-md shadow-sm"
                  id="modelName"
                  name="modelName"
                  value={formData.modelName}
                  onChange={(event) => handleChange(event, index)}
                  disabled={formData.doorName === ''}
                  required
                >
                  {/* Add options for Model Name */}
                </select>
              </div>

              {/* The rest of the form fields... */}
              {/* Replace this section with the remaining form fields */}
              {/* For example, you can have similar sections for Folio Name, Handle Name, Profil Name, Height, Width, Number, etc. */}

              <div className="col-span-2 flex justify-end">
                <button
                  className="w-full px-4 py-2 text-white bg-green-600 rounded-md shadow-md hover:bg-green-700"
                  onClick={handleAddGroup}
                  style={{ width: '150px', margin: '10px' }}
                >
                  Нова Група
                </button>
              </div>
            </form>
          </div>
        ))}
      </div>
      <NewOrderForm />
    </>
  );
};

export default NewExcelOrderForm;
