import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Button, TableContainer, Table, TableHeader, TableCell, TableRow, TableBody } from '@windmill/react-ui';
import PageTitle from "../components/Typography/PageTitle";
import labelsFile from "../assets/xlxs/за етикети.xlsx";
import json2xls from 'json2xls'; // Import json2xls
import { saveAs } from "file-saver"; // Import saveAs function
import * as XLSX from 'xlsx';
import ExcelJS from 'exceljs';
import { config } from '../Constants';


const SingleCustomer = ({ match }) => {
    const apiBaseUrl = config.url.API_BASE_URL;

    const [customer, setCustomer] = useState(null);
    const customerId = match.params.id; // Get the orderId from the route parameter
    const token = localStorage.getItem('accessToken')
    const loggedUser = JSON.parse(localStorage.getItem('user'))
    const formatDateWithoutDashes = (dateString) => {
        const date = new Date(dateString);
        const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
        return date.toLocaleDateString('en-US', options).replace(/\//g, ''); // Remove slashes
    };

    useEffect(() => {
        const fetchUser = async () => {

            try {
                const config = {
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                };
                const response = await axios.get(apiBaseUrl + `/api/customers/${customerId}`, config);
                setCustomer(response.data);
            } catch (error) {
                console.error('Error fetching order:', error);
            }
        };

        fetchUser();
    }, [customerId]);



    return (
        console.log(customer),
        <div className='border'>
            <div className='text-center border'>
                <PageTitle>Клиент:  {customer.companyName}
                </PageTitle>        </div>
            <div className='grid grid-cols-2 h-10 mb-4 '><div className='text-right border'><p className='mr-3'>ЕИК: {customer.bulstat}</p></div><div className='  text-left border'><p className='ml-3'>Номер: {customer.id}</p></div></div>
            <div className='grid grid-cols-2 border mb-4 '><div className='grid grid-cols-1 ml-3 mt-3 mb-3 space-y-[5px]'><div className='mb-2'>МОЛ : {customer.mol}</div><div className='mb-2'>Град : {customer.city}</div><div className='mb-2'>Адрес : {order.user.companyAddress}</div><div className='mb-2'>ЕИК/ВАТ : {order.user.bulstat}</div><div>МОЛ : {order.user.mol}</div></div><div className='grid grid-cols-1 border'><div className='ml-3 mt-3'>Телефон: {order.user.phone}</div><div className='ml-3'>Адрес на доставка : {order.user.companyAdress}</div></div></div>
            <div className='grid grid-cols-2'><h1 className='ml-3'>Материал : <span className='font-semibold'> {order.groups[0].door.name}</span></h1>{loggedUser.data.role === '[ADMIN]' && (
                <>
                    <div className='text-right mr-3 mb-4'>
                        <Button onClick={exportToExcel} className="">
                            Генериране на етикети
                        </Button>

                    </div>
                </>
            )}</div>
            <TableContainer>

                <Table>
                    <TableHeader>
                        <tr>
                            <TableCell>Модел</TableCell>
                            <TableCell>Фолио</TableCell>
                            <TableCell>Профил</TableCell>
                            <TableCell>Дръжка</TableCell>
                            <TableCell>Височина,мм</TableCell>
                            <TableCell>Широчина, мм</TableCell>
                            <TableCell>Брой</TableCell>
                            <TableCell>Цена Дръжка, бр.</TableCell>
                            <TableCell>Цена мат за кв.м</TableCell>
                            <TableCell>Двустр. ламиниране</TableCell>
                            <TableCell>Стойност</TableCell>

                            {/* Add more table headers for other fields */}
                        </tr>
                    </TableHeader>
                    <TableBody>
                        {order.groups.map((group, j) => (
                            <TableRow key={j}>

                                <TableCell>{group.model.name}</TableCell>
                                <TableCell>{group.folio.name}</TableCell>
                                <TableCell>{group.profil.name}</TableCell>
                                <TableCell>{group.handle.name}</TableCell>
                                <TableCell>{group.height}</TableCell>
                                <TableCell>{group.width}</TableCell>
                                <TableCell>{group.number}</TableCell>
                                <TableCell>{group.handle.price}</TableCell>
                                <TableCell>{group.matPrice}</TableCell>
                                <TableCell>{group.isBothSidesLaminated}</TableCell>
                                <TableCell>{group.groupTotalPrice}лв.</TableCell>

                            </TableRow>))}

                    </TableBody>

                </Table>
                <div ><p className='font-semibold content-end text-right mr-3 mt-3 mb-3'>Обща стойност : {order.totalPrice}лв.</p></div>
            </TableContainer>

        </div>
    );
};

export default SingleCustomer;
