import React, { Component } from 'react'
import { NavLink, Redirect } from 'react-router-dom'; 
import { Button, Form, Grid, Segment, Message } from 'semantic-ui-react'
import AuthContext from '../components/context/AuthContext'
import { orderApi } from '../components/misc/OrderApi'
import { parseJwt, handleLogError } from '../components/misc/Helpers'


class SignUp extends Component {
 static contextType = AuthContext;

  state = {
    username: '',
    password: '',
    name: '',
    email: '',
    isLoggedIn: false,
    isError: false,
    errorMessage: ''
  }

  componentDidMount() {
    const Auth = this.context;
    const isLoggedIn = Auth.userIsAuthenticated();
    this.setState({ isLoggedIn });
  }

  handleInputChange = (e, {name, value}) => {
    this.setState({ [name]: value })
  }

  handleSubmit = (e) => {
    e.preventDefault()

    const { username, password, name, email } = this.state
    if (!(username && password && name && email)) {
      this.setState({
        isError: true,
        errorMessage: 'Please, inform all fields!'
      })
      return
    }

    const user = { username, password, name, email }
    orderApi.signup(user)
      .then(response => {
        const { accessToken } = response.data
        const data = parseJwt(accessToken)
        const user = { data, accessToken }

        const Auth = this.context
        Auth.userLogin(user)

        this.setState({
          username: '',
          password: '',
          isLoggedIn: true,
          isError: false,
          errorMessage: ''
        })
      })
      .catch(error => {
        handleLogError(error)
        if (error.response && error.response.data) {
          const errorData = error.response.data
          let errorMessage = 'Invalid fields'
          if (errorData.status === 409) {
            errorMessage = errorData.message
          } else if (errorData.status === 400) {
            errorMessage = errorData.errors[0].defaultMessage
          }
          this.setState({
            isError: true,
            errorMessage
          })
        }
      })
  }

  render() {
    const { isLoggedIn, isError, errorMessage } = this.state

    if (isLoggedIn) {
      return <Redirect to='/app' />
    } else {
      return (
        <div className="flex items-center min-h-screen p-6 bg-gray-50 dark:bg-gray-900">
        <div className="flex-1 h-full max-w-4xl mx-auto overflow-hidden bg-white rounded-lg shadow-xl dark:bg-gray-800">
          <div className="flex flex-col overflow-y-auto md:flex-row">
            <div className="h-32 md:h-auto md:w-1/2">
              <img
                aria-hidden="true"
                className="object-cover w-full h-full dark:hidden"
                src={ImageLight}
                alt="Office"
              />
              <img
                aria-hidden="true"
                className="hidden object-cover w-full h-full dark:block"
                src={ImageDark}
                alt="Office"
              />
            </div>
            <main className="flex items-center justify-center p-6 sm:p-12 md:w-1/2">
              <div className="w-full">
                <h1 className="mb-4 text-xl font-semibold text-gray-700 dark:text-gray-200">Signup</h1>
                <Label>
                  <span>Username</span>
                  <Input
                    fluid
                    autoFocus
                    name="username"
                    icon="user"
                    iconPosition="left"
                    placeholder="Username"
                    onChange={this.handleInputChange}
                  />
                </Label>

                <Label className="mt-4">
                  <span>Password</span>
                  <Input
                    fluid
                    name="password"
                    icon="lock"
                    iconPosition="left"
                    placeholder="Password"
                    type="password"
                    onChange={this.handleInputChange}
                  />
                </Label>

                <Label className="mt-4">
                  <span>Name</span>
                  <Input
                    fluid
                    name="name"
                    icon="address card"
                    iconPosition="left"
                    placeholder="Name"
                    onChange={this.handleInputChange}
                  />
                </Label>

                <Label className="mt-4">
                  <span>Email</span>
                  <Input
                    fluid
                    name="email"
                    icon="at"
                    iconPosition="left"
                    placeholder="Email"
                    onChange={this.handleInputChange}
                  />
                </Label>

                <Button color="violet" fluid size="large" onClick={this.handleSubmit}>
                  Signup
                </Button>
              </div>
            </main>
          </div>
        </div>
      </div>
      )
    }
  }
}

export default SignUp
