// newExcelOrderForm.js
import React, { useState, useEffect } from 'react';
import { readFile } from 'xlsx';
import { utils } from 'xlsx';


const NewExcelOrderForm = () => {
  const [formData, setFormData] = useState({});
  const [error, setError] = useState(null);

  useEffect(() => {
    const readExcelFile = async () => {
      try {
        const workbook = XLSX.readFile('./src/assets/xlsx/Шаблон за поръчка Pi314LTD 2.xlsx');
        const sheet2 = workbook.Sheets['Sheet2'];

        const data = utils.sheet_to_json(sheet2);

        const formData = {};
        data.forEach((row) => {
          Object.keys(row).forEach((key) => {
            formData[key] = row[key];
          });
        });

        setFormData(formData);
      } catch (error) {
        setError(error.message);
      }
    };

    readExcelFile();
  }, []);

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <form>
      {Object.keys(formData).map((key) => (
        <div key={key}>
          <label>{key}</label>
          <input type="text" value={formData[key]} />
        </div>
      ))}
    </form>
  );
};

export default NewExcelOrderForm;
