import React from "react";
import { lazy } from "react";

const Dashboard = lazy(() => import("../pages/Dashboard"));
const Orders = lazy(() => import("../pages/Orders"));
const ProductsAll = lazy(() => import("../pages/ProductsAll"));
const SingleProduct = lazy(() => import("../pages/SingleProduct"));
const AddProduct = lazy(() => import("../pages/AddProduct"));
const Customers = lazy(() => import("../pages/Customers"));
const Chats = lazy(() => import("../pages/Chats"));
const Profile = lazy(() => import("../pages/Profile"));
const Settings = lazy(() => import("../pages/Settings"));
const Page404 = lazy(() => import("../pages/404"));
const Blank = lazy(() => import("../pages/Blank"));
const NewOrderForm = lazy(() => import("../pages/newOrderForm"));
const NewExcelOrderForm = lazy(() => import("../pages/newExcelOrderForm"));

// Custom hook to get user data and role from AuthContext
function useAuth() {
  const Auth = React.useContext(AuthContext);
  const user = Auth.getUser();
  const isAdmin = user.data.role[0] === "ADMIN";

  return { isAdmin };
}

const AdminRoutes = () => {
  const { isAdmin } = useAuth();

  const routes = isAdmin
    ? [
        {
          path: "/dashboard",
          component: Dashboard,
        },
        {
          path: "/orders",
          component: Orders,
        },
        {
          path: "/insert-by-excel",
          component: NewExcelOrderForm,
        },
        {
          path: "/insert-by-hand",
          component: NewOrderForm,
        },
        {
          path: "/all-products",
          component: ProductsAll,
        },
        {
          path: "/add-product",
          component: AddProduct,
        },
        {
          path: "/product/:id",
          component: SingleProduct,
        },
        {
          path: "/customers",
          component: Customers,
        },
        {
          path: "/chats",
          component: Chats,
        },
        {
          path: "/manage-profile",
          component: Profile,
        },
        {
          path: "/settings",
          component: Settings,
        },
        {
          path: "/404",
          component: Page404,
        },
        {
          path: "/blank",
          component: Blank,
        },
      ]
    : [
        {
          path: "/insert-by-hand",
          component: NewOrderForm,
        },
      ];

  return routes;
};

export default AdminRoutes;
