import React , { Component }from 'react'
import { Link, Navigate } from 'react-router-dom';
import ImageLight from '../assets/img/create-account-office.jpeg'
import ImageDark from '../assets/img/create-account-office-dark.jpeg'
import { GithubIcon, TwitterIcon } from '../icons'
import { Input, Label, Button } from '@windmill/react-ui'
import AuthContext from '../components/context/AuthContext'
import { orderApi } from '../components/misc/OrderApi'
import { parseJwt, handleLogError } from '../components/misc/Helpers'
import { Redirect } from 'react-router-dom/cjs/react-router-dom.min'


class CreateAccount extends Component{
  static contextType = AuthContext

  state = {
    username: '',
    password: '',
    name: '',
    email: '',
    isLoggedIn: false,
    isError: false,
    errorMessage: ''
  }

  componentDidMount() {
    const Auth = this.context
    const isLoggedIn = Auth.userIsAuthenticated()
    this.setState({ isLoggedIn })
  }

  handleInputChange = (e, {name, value}) => {
    this.setState({ [name]: value })
  }

  handleSubmit = (e) => {
    e.preventDefault()

    const { username, password, name, email } = this.state
    if (!(username && password && name && email)) {
      this.setState({
        isError: true,
        errorMessage: 'Please, inform all fields!'
      })
      return
    }

    const user = { username, password, name, email }
    orderApi.signup(user)
      .then(response => {
        const { accessToken } = response.data
        const data = parseJwt(accessToken)
        const user = { data, accessToken }

        const Auth = this.context
        Auth.userLogin(user)

        this.setState({
          username: '',
          password: '',
          isLoggedIn: true,
          isError: false,
          errorMessage: ''
        })
      })
      .catch(error => {
        handleLogError(error)
        if (error.response && error.response.data) {
          const errorData = error.response.data
          let errorMessage = 'Invalid fields'
          if (errorData.status === 409) {
            errorMessage = errorData.message
          } else if (errorData.status === 400) {
            errorMessage = errorData.errors[0].defaultMessage
          }
          this.setState({
            isError: true,
            errorMessage
          })
        }
      })

    const { isLoggedIn, isError, errorMessage } = this.state;
    // if (isLoggedIn) {
    //   return <Redirect to="/app" />;
    // } else {
      return (
        <div className="flex items-center min-h-screen p-6 bg-gray-50 dark:bg-gray-900">
          {/* Left section - Image */}
          <div className="flex-1 h-full max-w-4xl mx-auto overflow-hidden bg-white rounded-lg shadow-xl dark:bg-gray-800">
            <div className="flex flex-col overflow-y-auto md:flex-row">
              <div className="h-32 md:h-auto md:w-1/2">
                <img
                  aria-hidden="true"
                  className="object-cover w-full h-full dark:hidden"
                  src={ImageLight}
                  alt="Office"
                />
                <img
                  aria-hidden="true"
                  className="hidden object-cover w-full h-full dark:block"
                  src={ImageDark}
                  alt="Office"
                />
              </div>
              {/* Right section - Signup form */}
              <main className="flex items-center justify-center p-6 sm:p-12 md:w-1/2">
                <div className="w-full">
                  <h1 className="mb-4 text-xl font-semibold text-gray-700 dark:text-gray-200">
                    Create account
                  </h1>
                  {/* Signup form */}
                  <form onSubmit={this.handleSubmit}> {/* Add form tag */}
                    <Label>
                      <span>Email</span>
                      <Input
                        className="mt-1"
                        type="email"
                        name="email" 
                        placeholder="john@doe.com"
                        onChange={this.handleInputChange}
                      />
                    </Label>
                    <Label className="mt-4">
                      <span>Password</span>
                      <Input
                        className="mt-1"
                        type="password"
                        name="password" 
                        placeholder="***************"
                        onChange={this.handleInputChange}
                      />
                    </Label>
                    <Label className="mt-4">
                      <span>Confirm password</span>
                      <Input
                        className="mt-1"
                        type="password"
                        name="passwordConfirmation"
                        placeholder="***************"
                        onChange={this.handleInputChange}
                      />
                    </Label>

                    <Label className="mt-6" check>
                      <Input type="checkbox" />
                      <span className="ml-2">
                        I agree to the <span className="underline">privacy policy</span>
                      </span>
                    </Label>

                    {/* Submit button */}
                    <Button type="submit" block className="mt-4"> {/* Add type="submit" */}
                      Create account
                    </Button>
                  </form>
                  {/* Error message */}
                  {isError && <p className="mt-4 text-red-600">{errorMessage}</p>}
                  {/* ...Other JSX code */}
                </div>
              </main>
            </div>
          </div>
        </div>
      );
    }
  }
// }


export default CreateAccount
