import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  TableBody,
  TableContainer,
  Table,
  TableHeader,
  TableCell,
  TableRow,
  TableFooter,
  Avatar,
  Badge,
  Pagination,
} from "@windmill/react-ui";
import { Link } from 'react-router-dom'
import json2xls from 'json2xls'; // Import json2xls
import { saveAs } from "file-saver"; // Import saveAs function
import * as XLSX from 'xlsx';




const OrdersTable = ({ resultsPerPage, filter }) => {
  const [page, setPage] = useState(1);
  const [data, setData] = useState([]);
  const [totalResults, setTotalResults] = useState(0);
  const token = localStorage.getItem('accessToken')

  // pagination change control
  function onPageChange(p) {
    setPage(p);
  }

  const fetchData = async () => {
    try {
      const config = {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      };
      const response = await axios.get("http://localhost:8080/api/orders/all", config);
      const ordersData = response.data;

      // Apply filters if necessary
      let filteredData = ordersData;
      if (filter === "изпратена") {
        filteredData = ordersData.filter(order => order.status === "Paid");
      } else if (filter === "създадена") {
        filteredData = ordersData.filter(order => order.status === "CREATED");
      } else if (filter === "изпълнява се") {
        filteredData = ordersData.filter(order => order.status === "WORKING_ON");
      } else if (filter === "изпълнена") {
        filteredData = ordersData.filter(order => order.status === "Paid");
      }

      // Flatten the groups and update the totalResults and data state
      const flattenedGroups = filteredData.flatMap(order => order);
      setTotalResults(flattenedGroups.length);
      setData(flattenedGroups.slice((page - 1) * resultsPerPage, page * resultsPerPage));
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };


  const exportToExcel = () => {
    // Create an array to store the Excel rows
    const excelRows = [];
    excelRows.push([
      "ПОРЕДЕН НОМЕР", "Група", "ДАТА", "ПОРЪЧКА", "КЛИЕНТ", "ВИД", "ГРАД",
      "ЕТИКЕТ", "МОДЕЛ", "ВИСОЧИНА", "ШИРИНА", "БРОЙ КОЛИЧЕСТВО",
      "МЕРНА ЕДИНИЦА", "ДРЪЖКА", "ЕДИНИЧНА ЦЕНА", "ЦЕНА НА ДРЪЖКА", "ФОЛИРАНЕ",
      "ДВУСТРАННО ФОЛИРАНЕ"
    ]);

    data.forEach(order => {
      order.groups.forEach(group => {
        const row = [
          order.createdAt + order.id, "Група", order.createdAt, order.id, order.user.companyName, "КЛИЕНТ-МОБ.ПРИЛОЖЕНИЕ",
          "СОФИЯ", "вратичка", group.model.name, group.height, group.width, group.number,
          "квадратен метър", group.handle.name, group.handle.price, "Цена на Дръжка", group.folio.name,
          group.bothSidesLaminated ? "ДВУСТРАННО" : ""
        ];
        excelRows.push(row);
      });
    });

    const worksheet = XLSX.utils.aoa_to_sheet(excelRows);
    const workbook = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(workbook, worksheet, 'Orders');

    const wbout = XLSX.write(workbook, { bookType: 'xlsx', type: 'array' });
    const blob = new Blob([wbout], { type: 'application/octet-stream' });

    saveAs(blob, 'orders.xlsx');
  };



  useEffect(() => {
    fetchData();
  }, [page, resultsPerPage, filter]);
  return (
    <div>
      {/* Table */}
      <TableContainer className="mb-8">
        <Table>
          <TableHeader>
            <tr>
              <TableCell>id</TableCell>
              <TableCell>Oт дата</TableCell>
              <TableCell>Статус</TableCell>
              <TableCell>Потребител</TableCell>
              <TableCell>Крайна сума</TableCell>
              <TableCell>Тип поръчка</TableCell>
              <TableCell>Отстъпка</TableCell>





            </tr>
          </TableHeader>
          <TableBody>

            {data.map((order, i) => (
              <TableRow key={i}>
                <TableCell>
                  <div className="flex items-center text-sm">
                    <div>
                      <p className="font-semibold">{order.id}</p>
                    </div>
                  </div>
                </TableCell>
                <TableCell>
                  <div className="flex items-center text-sm">
                    <div>
                      <p className="font-semibold">{order.createdAt}</p>
                    </div>
                  </div>
                </TableCell>
                <TableCell>
                  <Badge
                    type={
                      order.status === "CREATED"
                        ? "success"
                        : order.status === "Paid"
                          ? "success"
                          : order.status === "WORKING_ON"
                            ? "warning"
                            : "neutral"
                    }
                  >

                    {order.status === "WORKING_ON" ? "Изпълнява се"  : order.status === "CREATED" ? "Създадена  " : order.status  }


                  </Badge>
                </TableCell>
                <TableCell>

                  <div className="flex items-center text-sm">
                    <div>
                      <p className="font-semibold">{order.user.email}</p>
                    </div>
                  </div>
                </TableCell>

                <TableCell>
                  <div className="flex items-center text-sm">
                    <div>
                      <span className="font-semibold">{order.totalPrice}</span  ><span>лв.</span>
                    </div>
                  </div>
                </TableCell>


                <TableCell>
                  {order.type === "BY_HAND" ? "Ръчно - от Администратор" : "От клиент"}
                </TableCell>
                <TableCell>
                  <div className="flex items-center text-sm">
                    <div>
                      <span className="font-semibold">0</span  ><span>%</span>
                    </div>
                  </div>
                </TableCell>
                <TableCell className="w-1/12 text-center">
                  <Link
                    to={`orders/${order.id}`}
                    className="text-indigo-600 hover:text-indigo-900"
                  >
                    Детайли
                  </Link>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
        <TableFooter>
        <div className="mt-4">
        <button onClick={exportToExcel} className="btn btn-primary">Export to Excel</button>
      </div>
          <Pagination
            totalResults={totalResults}
            resultsPerPage={resultsPerPage}
            label="Table navigation"
            onChange={onPageChange}
          />
        </TableFooter>
      </TableContainer>
    </div>
  );
};

export default OrdersTable;
